import {Component, ElementRef, OnInit, Renderer2} from '@angular/core';
import {Fetcher} from "../../tools/Fetcher";
import {Item} from "../../model/Item";
import champions from "../../tools/champions.json"
import {Champion} from "../../model/Champion";

@Component({
  selector: 'app-builds',
  templateUrl: './builds.component.html',
  styleUrls: ['./builds.component.css']
})
export class BuildsComponent implements OnInit{
  items: Item[] = [];
  champions: Champion[] = [];
  roles: string[] = [
    'Top',
    'Jungle',
    'Mid',
    'ADC',
    'Support'
  ]
  currentBuilds: any;
  numBuilds: number = 1;
  constructor(private el: ElementRef, private renderer: Renderer2) {
  }
  ngOnInit(): void {
    const fetcher = new Fetcher();
    this.items = fetcher.items;
    for(let champ of champions){
      this.champions.push(
        {name: champ.name,
        icon: this.parseIcon(champ.id)
        }
      )
    }
  }
  parseIcon(id: string): string{
    return `https://ddragon.leagueoflegends.com/cdn/11.16.1/img/champion/${id}.png`
  }
  generateBuilds(number: number){
    if (number <= 0) {
      return [];
    }
    const uniqueChampions = [...this.champions];
    const uniqueRoles = [...this.roles];
    const builds = [];
    for (let i = 0; i < number; i++) {
      if (uniqueChampions.length === 0 || uniqueRoles.length === 0) {
        break;
      }
      const randomChampionIndex = Math.floor(Math.random() * uniqueChampions.length);
      const randomRoleIndex = Math.floor(Math.random() * uniqueRoles.length);
      const champion = uniqueChampions.splice(randomChampionIndex, 1)[0];
      const role = uniqueRoles.splice(randomRoleIndex, 1)[0];
      const items = this.generateItems();
      builds.push({
        champion: champion,
        role: role,
        items: items
      });
    }
    console.log(builds);
    this.currentBuilds = builds;
    return builds;
  }
  generateItems(): Item[]{
    let items = [];
    let boots = this.items.filter((item: Item) => item.type === "Boots");
    let mythics = this.items.filter((item: Item) => item.type === "Mythic");
    let uniqueLegendaries = this.items.filter((item: Item) => item.type === "Legendary")

    items.push(boots[Math.floor(Math.random() * boots.length)]);
    items.push(mythics[Math.floor(Math.random() * mythics.length)]);
    for(let i = 0; i < 4; i++){
      let randomIndex = Math.floor(Math.random() * uniqueLegendaries.length);
      items.push(uniqueLegendaries[randomIndex]);
      uniqueLegendaries.splice(randomIndex, 1);

    }

    return items;
  }


}
<div class="board">
  <div class="controls">
    <div class="range">
      <p class="num-builds">  Number of Builds: {{numBuilds}} </p>
      <input type="range" min="1" max="5" [(ngModel)]="numBuilds">
    </div>
    <button (click)="generateBuilds(numBuilds)" class="gen-btn"> GIVE ME TROLL BUILDS ðŸ˜ˆ </button>
  </div>

  <div class="builds">
    <div class="build" *ngFor="let build of currentBuilds">
      <div class="champion">
        <img src="assets/SingedSquare.webp" width="100px" height="100px">
        <p class="p-desc"> {{build.champion.name}} </p>
      </div>
      <div class="role">
        <p class="p-desc">  {{build.role}} </p>
      </div>
      <div class="items">
        <div class="item" *ngFor="let item of build.items">
          <img src="{{item.icon}}" width="64px" height="64px">
        </div>
      </div>
    </div>
  </div>
</div>
