import { Component } from '@angular/core';
import {Router} from "@angular/router";

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {
  constructor(private router: Router){

  }
  goto(url: string) {
    this.router.navigate([url]);
  }
}
<div class="container">
  <div class="title">
    <h1 (click)="goto('')"> League Of Trolls </h1>
  </div>
  <div class="navigation">
    <p class="link" (click)="goto('builds')"> BUILDS </p>
  </div>
  <div class="other">
    <p class="version-text"> 1.0.0 </p>
    <p class="author"> created by: <a href="https://github.com/JakubKonkol"> JakubKonkol </a></p>
  </div>
</div>
